mapper.select:
  		SELECT COUNT(*)
  		FROM MY_PET
	  	WHERE EQUIP_NUM = #{inventory_num}

SELECT COUNT(*)
FROM MY_PET
WHERE ITEM_NUM = #{item_num}

mapper.update:
UPDATE 
ITEM SET
ITEM_NAME=#{item_name}, ITEM_DESCRIPTION=#{item_description}, ITEM_HP=#{item_hp}, ITEM_ATK=#{item_atk}, ITEM_DOD=#{item_dod}, ITEM_PRICE=#{item_price}
WHERE ITEM_NUM = #{item_num}

UPDATE
ITEMFILE SET
FILE_NAME=#{file_name}, ORI_NAME=#{ori_name}
WHERE FILE_NUM = #{file_num}
